return [
  {
    source_of_truth: {
      state: "ninguno",
      available_slots: [],
      recommended_slots: [],
      confirmated_date_time: "",
      meet_data: "",
    }
  }
];

// Get all items
const items = $input.all();

// Map only type and content, checking message exists
const chat_history = items
  .map(item => {
    const msg = item.json?.message;
    if (!msg) return null; // ignora si no hay message
    return {
      type: msg.type || "",
      content: msg.content || ""
    };
  })
  .filter(Boolean); // elimina los null

return { chat_history };

